import { createSlice } from '@reduxjs/toolkit';

export const itemSlice = createSlice({ 
    name:'item',
    item: [],
    initialState: {
        item: [],
    },
    reducers: {
        additem: (state, action) => {
            state.name = action.payload;
        },
    },
});

export const { additem } = itemSlice.actions; 

export default itemSlice.reducer;
