import React,{useState,useEffect} from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import './MyOrders.css'



const MyOrders=()=>{
    const navigate = useNavigate()
    let car =[]
    const [nome,setNome] = useState('')
    const [quantety,setQuantety] = useState()
    const [value,setValue] = useState()
    const [searchNome,setSearchNome] = useState('')

    const [page, setPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [hasnextpage, setHasNextPageSize] = useState(true);


    const search_name_change_handler =(event)=>{ setSearchNome(event.target.value)}


    const accessToken = localStorage.getItem('accessToken');


    //todo para a busca de itens 

    const searchSubmitHandler = async (event) => {
        event.preventDefault();
        const data = {
            name: searchNome, 
            page: page,
            pageSize: pageSize,
        };

        const config = {
            params: data,
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('accessToken')}`
            }
        };

        try {
            const request = await axios.get('http://localhost:3000/agents/items', config);
            renderTable(request.data, document.getElementById('tabelaItens'))

        } catch (error) {
            console.log(error);
        }
    }

    async function fetchItems() { 
        const params = {
            name: nome, 
            page: page,
            pageSize: pageSize,
        };
        const config = {
            params,
            headers: {
                'Authorization': `Bearer ${accessToken}`
            }
        };
        try {
            const response = await axios.get('http://localhost:3000/agents/items', config);
            if(response.data.length >= pageSize){
                setHasNextPageSize(false)
            }else{
                setHasNextPageSize(true)
            }

            const items = response.data;
            renderTable(items, document.getElementById('tabelaItens'));
        } catch (error) {
            console.error('Ocorreu um erro:', error);
        }
    }

    function renderTable(items, container) {
        container.innerHTML = "";

        const table = document.createElement('table');
        const tbody = document.createElement('tbody');
        const headers = ['id', 'Nome','Quantidade', 'Valor','Add'];

        const thead = document.createElement('thead');
        const headerRow = document.createElement('tr');
        headers.forEach(headerText => {
            const th = document.createElement('th');
            th.textContent = headerText;
            headerRow.appendChild(th);
        });
        thead.appendChild(headerRow);
        table.appendChild(thead);



        items.forEach(item => {
            const row = document.createElement('tr');
            Object.values(item).forEach(value => {
                const cell = document.createElement('td');
                cell.textContent = value;
                row.appendChild(cell);
            });
            const addButton = document.createElement('button');
                    addButton.textContent = 'Adicionar';
                    addButton.addEventListener('click', (event) => {
                        const clickedButton = event.target;

                        const selectedItem = item;
                        console.log('Adicionar item:', item);
                        addItem(selectedItem);
                    });

            const addRemoveButton = document.createElement('button');
                addRemoveButton .textContent = 'Remover';
                addRemoveButton .addEventListener('click', (event) => {
                    const clickedButton = event.target;
                    const selectedItem = item;
                    console.log('Adicionar item:', item);
                    RemoveItem(selectedItem);
                });

            row.appendChild(addButton);
            row.appendChild(addRemoveButton);
            tbody.appendChild(row);
        });
        table.appendChild(tbody);
        container.appendChild(table);
    }

    useEffect(() => {
        fetchItems();
    }, [[page, pageSize]]); 

    const handleNextPage = () => {
        setPage(page + 1);
      };

      const handlePreviousPage = () => {
        setPage(page - 1);
      };


    useEffect(() => {
        createList();
    }, []); 

    const orderSubmit = async () => {
        const config = {
            headers: {
                Authorization: `Bearer ${accessToken}`,
            }
        };
        try {
            const response = await axios.post('http://localhost:3000/costumers/order',{item:car},config);
            const List =document.getElementById('car');
            while (List.firstChild) {
                List.removeChild(List.firstChild);
            }
            alert("Ordem efetuada com sucesso")

        } catch (error) {
            alert("Ocorreu um erro")
            console.error('Ocorreu um erro:', error);
        }

      };

    const createList=()=>{
        const List =document.getElementById('MyOrders-car');
        const list = document.createElement('ul');
        list.id = 'car'; 
        List.appendChild(list)
    }

    function addItem(item){
        const List = document.getElementById('car');
        const listItem = document.createElement('li');
        listItem.textContent = `ID:${item.id},:Nome:${item.name}, Quantidade: 1, Valor: ${item.value}`;


        const newItem = { id: item.id, Nome: item.name, Quantidade: 1, Valor: item.value };


        car.push(newItem)

        console.log(car)
        List.appendChild(listItem);
    }
    function RemoveItem(itemToRemove) {
        // Filter out the item to remove
        console.log("teste",itemToRemove)
        const exists = car.some(item => item.id == itemToRemove.id && item.name == itemToRemove.name);
        console.log(exists)
        if (exists) {
            console.log("Item exists in the array");
        } else {
            console.log("Item does not exist in the array");
        }
        for(let i =0 ; i <car.length;i++){
            if(car[i].id == itemToRemove.id){
                console.log(car[i])
                console.log("igual")
            }else{console.log("diferente")}
        }


        // Remove the corresponding list item from the DOM
        const list = document.getElementById('car');
        const items = list.getElementsByTagName('li');
        for (let i = 0; i < items.length; i++) {
            if (items[i].textContent === itemToRemove) {
                items[i].remove();
                break;
            }
        }

        console.log("Removed item:", itemToRemove);
        console.log("Updated car array:", car);
    }


    return(
        <>
        <h1>NEW ORDER</h1>
        <div className="MyOrders">
            <div className="searchMotor">
                <div className="MyOrders-search">
                        <h1 className="card-header">Search item</h1>
                        <form className="add_item search" onSubmit={searchSubmitHandler}> 
                            <div className="card-content search">
                                <label>Nome</label>
                                <input type="text" value={searchNome} onChange={search_name_change_handler}></input>
                            </div>

                            <div className="card-footer">
                                <button type="submit" >Search</button>
                            </div>
                        </form>
                </div>
                <div className="MyOrders-table">
                    <div id="tabelaItens"></div>
                    <div>
                        <button onClick={handlePreviousPage} disabled={page === 1}>Página Anterior</button>
                        <button onClick={handleNextPage} disabled={hasnextpage}>Próxima Página</button>
                    </div>
                </div>
            </div>
            <div id="MyOrders-car" className="MyOrders-car">
                <h1>carrinho</h1>
                <button onClick={orderSubmit} >New Order</button>
            </div>

        </div>
        </>
    )}
export default MyOrders
