import React,{useState,useEffect} from "react";
import axios from "axios";
import '../item/add_item.css'

const Orders =() =>{

    const [page, setPage] = useState(1);
    const [pageSize, setPageSize] = useState(5);
    const accessToken = localStorage.getItem('accessToken');
    const [hasnextpage, setHasNextPageSize] = useState(true);

    async function fetchItems() {

        const params = {
            page: page,
            pageSize: pageSize,
        };
        const config = {
            params,
            headers: {
                'Authorization': `Bearer ${accessToken}`
            }
        };
        try {
            const response = await axios.get('http://localhost:3000/agents/orders', config);
            response.data.forEach(item => delete item.createdAt);
            response.data.forEach(item => delete item.updatedAt);

            //console.log("find user",response.data.user_id)
            //const costumer = await axios.get('http://localhost:3000/agents/costumer',config,response.data.user_id);
            //console.log(costumer)
            //console.log(response.data)


            const orders = response.data; // Dados das ordens obtidas na primeira solicitação

            // Para cada ordem, faça uma solicitação para obter o nome do usuário com base no user_id
            for (const order of orders) {
                try {
                    const userResponse = await axios.get(`http://localhost:3000/agents/user/${order.user_id}`, config);
                    const user = userResponse.data;
                    console.log(`Nome do usuário para a ordem com ID ${order.id}: ${user.name}`);
                    // Faça algo com o nome do usuário, como adicioná-lo à ordem
                    order.userName = user.name; // Adicionando o nome do usuário à ordem (supondo que o nome do usuário seja armazenado em user.name)
                } catch (error) {
                    console.error(`Falha ao obter o nome do usuário para a ordem com ID ${order.id}: ${error.message}`);
                }
            }
            if(response.data.length >= pageSize){
                setHasNextPageSize(false)
            }else{
                setHasNextPageSize(true)
            }

            console.log(response)
            const items = response.data;

            renderTable(items, document.getElementById('tabelaOrdens'));
        } catch (error) {
            console.error('Ocorreu um erro:', error);
        }
    }

    function renderTable(items, container) {
        container.innerHTML = "";

        const table = document.createElement('table');
        const tbody = document.createElement('tbody');
        const headers = ['id', 'Nome','Valor total', 'local'];

        const thead = document.createElement('thead');
        const headerRow = document.createElement('tr');
        headers.forEach(headerText => {
            const th = document.createElement('th');
            th.textContent = headerText;
            headerRow.appendChild(th);
        });
        thead.appendChild(headerRow);
        table.appendChild(thead);

        items.forEach(item => {
            const row = document.createElement('tr');
            Object.values(item).forEach(value => {
                const cell = document.createElement('td');
                cell.textContent = value;
                row.appendChild(cell);
            });
            tbody.appendChild(row);
        });

        table.appendChild(tbody);
        container.appendChild(table);
    }

    useEffect(() => {
        fetchItems();
    }, [[page, pageSize]]); 

    const handleNextPage = () => {
        setPage(page + 1);
      };

      const handlePreviousPage = () => {
        setPage(page - 1);
      };


    return(
        <div>
             <h1>orders</h1>
            <div id="tabelaOrdens"></div>
            <div>
                <button onClick={handlePreviousPage} disabled={page === 1}>Página Anterior</button>
                <button onClick={handleNextPage} disabled={hasnextpage}>Próxima Página</button>
            </div>
        </div>

    )
}

export default Orders
