import { createSlice } from '@reduxjs/toolkit';

export const carSlice = createSlice({ 
  name: 'carrinhoCompra',
  initialState: {
    list: [],
  },
  reducers: {
    addCar: (state, action) => {
      state.list.push(action.payload);
    },
    removeCar: (state, action) => {
      state.list = state.list.filter(car => car.id !== action.payload.id); 
    },
    updateCar: (state, action) => {
      state.list = state.list.map((car) => {
          if (car.id === action.payload.id) {
              // Se encontrarmos o item no carrinho, incrementamos sua quantidade em 1
              return {
                  ...car,
                  quantity: car.quantity + 1
              };
          } else {
              // Se o item não for o que estamos procurando, mantemos o item inalterado
              return car;
          }
      });
  },

  },
});

export const { addCar, removeCar, updateCar } = carSlice.actions; 

export default carSlice.reducer;
