import React, { useState,useCallback } from 'react';    
import AddItem from "../item/additem";
import Orders from "../orders/orders";
import { Jwt } from 'jsonwebtoken';
//import dotenv from 'dotenv';

import '../menu/sidemenu.css'
import MyOrders from '../orders/my_orders';
import Orders_Id from '../orders/orders_id';

const HomePage =()  =>{
    // Recuperando o token do Local Storage
    const accessToken = localStorage.getItem('accessToken');
    dotenv.config();
    const secretKey = 'super-top-secret'
    const decodedToken = jwt.decode(accessToken, secretKey);

    console.log(decodedToken)
    const [isOpen, setIsOpen] = useState(false);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);

    const toggleMenu = useCallback(() => {
        setIsOpen(!isOpen);
    }, [isOpen]);

    const handleMenuItemClick = (menuItem) => {
        setSelectedMenuItem(menuItem);
    };

    let contentComponent;
    switch (selectedMenuItem) {
        case 'Items':
            contentComponent = <AddItem />;
            break;
        case 'New Order':
            contentComponent = <MyOrders />;
            break;
        case 'My Orders':
            contentComponent = <Orders_Id/>;
            break;
        case 'All Orders':
            contentComponent = <Orders/>;
            break;
        default:
            contentComponent = null;
    }


    return(
        <div>
            <div className="menu">
                <div className={`sidebar ${isOpen ? 'open' : ''}`}>
                    <ul>
                        <li onClick={() => handleMenuItemClick('Items')}>Items</li>
                        <li onClick={() => handleMenuItemClick('New Order')}>New Orders</li>
                        <li onClick={() => handleMenuItemClick('My Orders')}>My Orders</li>
                        <li onClick={() => handleMenuItemClick('All Orders')}>All Orders</li>
                        <li className="rodape">Diego Sousa nest tutorial</li>
                    </ul>
                </div>

                <div className={`content ${isOpen ? 'open' : ''}`}>
                    <button onClick={toggleMenu}>Toggle Menu</button>
                    <p>Main Content</p>
                    <div>
                        {contentComponent}
                    </div>
                </div>
            </div>

        </div>
    )
}

export default HomePage
