import React, { useState,useCallback } from 'react';    
import AddItem from "../item/additem";
import Orders from "../orders/orders";
import SideMenu from "../menu/sideMenu";


const HomePage =()  =>{
    // Recuperando o token do Local Storage
    const accessToken = localStorage.getItem('accessToken');
    const [isOpen, setIsOpen] = useState(false);
    const [selectedMenuItem, setSelectedMenuItem] = useState(null);

    const toggleMenu = useCallback(() => {
        setIsOpen(!isOpen);
    }, [isOpen]);

    const handleMenuItemClick = (menuItem) => {
        setSelectedMenuItem(menuItem);
    };

    let contentComponent;
    switch (selectedMenuItem) {
        case 'Items':
            contentComponent = <AddItem />;
            break;
        case 'My Orders':
            contentComponent = <Orders />;
            break;
        case 'All Orders':
            // Renderizar outro componente aqui
            break;
        default:
            contentComponent = null;
    }


    return(
        <div>
            <div className="menu">
                <div className={`sidebar ${isOpen ? 'open' : ''}`}>
                    <ul>
                    <li onClick={() => handleMenuItemClick('Items')}>Items</li>
                    <li onClick={() => handleMenuItemClick('My Orders')}>My Orders</li>
                    <li onClick={() => handleMenuItemClick('All Orders')}>All Orders</li>
                    <li onClick={() => handleMenuItemClick('Item 4')}>Item 4</li>
                </ul>
                </div>

                <div className="content">
                    <button onClick={toggleMenu}>Toggle Menu</button>
                    <p>Main Content</p>
                    <div>
                        <AddItem></AddItem>
                        <Orders></Orders>
                    </div>
                    <div>
                        {contentComponent}
                    </div>
                </div>
            </div>

        </div>
    )
}

export default HomePage
